mapper.select:
select count(1) from gds_goods g , gds_brand b , gds_classify c , gds_classify cp where 1 = 1 and g.brandID = b.id and g.classifyID = c.id and c.pid = cp.id
and cp.id = #{classifyPID}
and g.classifyID = #{classifyID}
and g.brandID = #{brandID}
and g.name like concat('%', #{name}, '%')

select g.* , b.name as brand , c.name as classify , cp.name as classifyPname from gds_goods g , gds_brand b , gds_classify c , gds_classify cp where 1 = 1 and g.brandID = b.id and g.classifyID = c.id and c.pid = cp.id
and cp.id = #{classifyPID}
and g.classifyID = #{classifyID}
and g.brandID = #{brandID}
and g.name like concat('%', #{name}, '%')
order by cp.id asc , c.id asc , b.id asc , g.id desc limit #{page}, #{rows};

mapper.insert:
insert into 
	gds_goods(
		name
		, descr
		, mPrice
		, cPrice
		, weight
		, salds
		, stock
		, brandID
		, classifyID
		, creator
		, createTime
	)
	values(
		#{name}
		, #{descr}
		, #{mPrice}
		, #{cPrice}
		, #{weight}
		, 0
		, #{stock}
		, #{brandID}
		, #{classifyID}
		, #{creator}
		, #{createTime}
	);

insert into 
	gds_image(
		orgPath
		, smaPath
		, midPath
		, bigPath
		, sortNum
		, goodsID
		, category
		, creator
		, createTime
	)
	values
	
		(
			#{i.orgPath}
			, #{i.smaPath}
			, #{i.midPath}
			, #{i.bigPath}
			, #{i.sortNum}
			, #{i.goodsID}
			, #{i.category}
			, #{i.creator}
			, #{i.createTime}
		)
	
	;

mapper.update:
update 
	gds_goods
set
	name = #{name}
	, descr = #{descr}
	, mPrice = #{mPrice}
	, cPrice = #{cPrice}
	, weight = #{weight}
	, stock = #{stock}
	, brandID = #{brandID}
	, classifyID = #{classifyID}
	, modifier = #{modifier}
	, modifyTime = #{modifyTime}
where
	id = #{id};

update 
	gds_image 
set 
	orgPath = #{orgPath}
	, bigPath = #{bigPath}
	, midPath = #{midPath}
	, smaPath = #{smaPath}
	, modifier = #{modifier} 
	, modifyTime = #{modifyTime}
where id = #{id};

mapper.insert:
insert into 
	gds_image(orgPath, smaPath, midPath, bigPath, goodsID, category, creator, createTime, sortNum)
	values(#{orgPath}, #{smaPath}, #{midPath}, #{bigPath}, #{goodsID}, #{category}, #{creator}, #{createTime}
		, (select max(sortNum)+1 from gds_image t where goodsID = #{goodsID} and category = #{category}));
