mapper.select:
select count(g.id) from gds_goods g where 1 = 1
and g.classifyID = #{classifyID}
and g.brandID = #{brandID}
and g.name like concat('%', #{name}, '%')

select g.* , b.name as brand , c.name as classify from gds_goods g , gds_brand b , gds_classify c where 1 = 1
and g.classifyID = #{classifyID}
and g.brandID = #{brandID}
and g.name like concat('%', #{name}, '%')
and g.brandID = b.id and g.classifyID = c.id order by g.id desc limit #{page}, #{rows};
