mapper.select:
select b.* , c.username as creatorName , m.username as modifierName from gds_brand b left join sys_user c on b.creator = c.id left join sys_user m on b.modifier = m.id where 1 = 1
and b.name like concat('%', #{name}, '%')
order by cast(b.sort as unsigned int) asc limit #{page}, #{rows};

select count(1) from gds_classify b where b.level = #{level}
and b.pid = #{pid}
and b.name like concat('%', #{name}, '%')

select b.* , c.username as creatorName , m.username as modifierName from gds_classify b left join sys_user c on b.creator = c.id left join sys_user m on b.modifier = m.id where b.level = #{level}
and b.pid = #{pid}
and b.name like concat('%', #{name}, '%')
order by cast(b.sort as unsigned int) asc limit #{page}, #{rows};

select count(1) from gds_goods g , gds_brand b , gds_classify c , gds_classify cp where 1 = 1 and g.brandID = b.id and g.classifyID = c.id and c.pid = cp.id
and cp.id = #{classifyPID}
and g.classifyID = #{classifyID}
and g.brandID = #{brandID}
and g.name like concat('%', #{name}, '%')

select g.* , b.name as brandName , c.name as classifyName , cp.id as classifyPID , cp.name as classifyPName from gds_goods g , gds_brand b , gds_classify c , gds_classify cp where 1 = 1 and g.brandID = b.id and g.classifyID = c.id and c.pid = cp.id
and cp.id = #{classifyPID}
and g.classifyID = #{classifyID}
and g.brandID = #{brandID}
and g.name like concat('%', #{name}, '%')
order by g.createTime desc limit #{page}, #{rows};

SELECT COUNT(1) FROM gds_order b WHERE 1 = 1
AND b.payID = #{payID}
AND b.orderNo = #{orderNo}

SELECT b.*, m.nickName AS buyerName, a1.name AS addrProvinceName, a2.name AS addrCityName, a3.name AS addrCountyName FROM gds_order b LEFT JOIN sys_member m ON b.buyerID = m.id LEFT JOIN sys_area a1 ON b.addrProvince = a1.code LEFT JOIN sys_area a2 ON b.addrCity = a2.code LEFT JOIN sys_area a3 ON b.addrCounty = a3.code WHERE 1 = 1
AND b.payID = #{payID}
AND b.orderNo = #{orderNo}
ORDER BY b.createTime DESC LIMIT #{page}, #{rows};

mapper.insert:
insert into 
	gds_goods(
		name
		, descr
		, mPrice
		, cPrice
		, weight
		, salds
		, stock
		, brandID
		, classifyID
		, creator
		, createTime
	)
	values(
		#{name}
		, #{descr}
		, #{mPrice}
		, #{cPrice}
		, #{weight}
		, 0
		, #{stock}
		, #{brandID}
		, #{classifyID}
		, #{creator}
		, #{createTime}
	);

insert into 
	gds_image(
		orgPath
		, smaPath
		, midPath
		, bigPath
		, sortNum
		, goodsID
		, category
		, creator
		, createTime
	)
	values
	
		(
			#{i.orgPath}
			, #{i.smaPath}
			, #{i.midPath}
			, #{i.bigPath}
			, #{i.sortNum}
			, #{i.goodsID}
			, #{i.category}
			, #{i.creator}
			, #{i.createTime}
		)
	
	;

mapper.update:
update 
	gds_goods
set
	name = #{name}
	, descr = #{descr}
	, mPrice = #{mPrice}
	, cPrice = #{cPrice}
	, weight = #{weight}
	, stock = #{stock}
	, brandID = #{brandID}
	, classifyID = #{classifyID}
	, modifier = #{modifier}
	, modifyTime = #{modifyTime}
where
	id = #{id};

update 
	gds_image 
set 
	orgPath = #{orgPath}
	, bigPath = #{bigPath}
	, midPath = #{midPath}
	, smaPath = #{smaPath}
	, modifier = #{modifier} 
	, modifyTime = #{modifyTime}
where id = #{id};
