mapper.select:
select * from sys_member where 1=1

	and mobile = #{mobile}


	and id = #{id}

order by createTime limit 1;

mapper.update:
update sys_member
sex = #{sex}, email = #{email}, hobby = #{hobby}, nickName = #{nickName}, birthday = #{birthday}, modifier = #{modifier}, modifyTime = #{modifyTime},
where id = #{id};

update sys_addr
name = #{name}, mobile = #{mobile}, telephone = #{telephone}, province = #{province}, city = #{city}, county = #{county}, zipcode = #{zipcode}, address = #{address}, isdefault = #{isdefault}, modifier = #{modifier}, modifyTime = #{modifyTime},
where id = #{id} and memberID = #{memberID};

mapper.select:
select addr.*, a1.name AS provinceName, a2.name AS cityName, a3.name AS countyName from sys_addr addr LEFT JOIN sys_area a1 ON addr.province = a1.code LEFT JOIN sys_area a2 ON addr.city = a2.code LEFT JOIN sys_area a3 ON addr.county = a3.code where enable = '1' and exist = '1' and memberID = #{memberID}
and id = #{id}
order by isdefault + '' desc;
